const PlasmicDescendants = {
  root: [
    "root",
    "header",
    "apiRequest",
    "swiperSlider",
    "topics",
    "iframe",
    "link",
    "embedHtml",
    "progress",
    "comment",
    "button"
  ],
  header: ["header"],
  apiRequest: [
    "apiRequest",
    "swiperSlider",
    "topics",
    "iframe",
    "link",
    "embedHtml",
    "progress",
    "comment"
  ],
  swiperSlider: ["swiperSlider"],
  topics: ["topics"],
  iframe: ["iframe"],
  link: ["link"],
  embedHtml: ["embedHtml"],
  progress: ["progress"],
  comment: ["comment"],
  button: ["button"]
} as const;
type NodeNameType = keyof typeof PlasmicDescendants;
type DescendantsType<T extends NodeNameType> =
  (typeof PlasmicDescendants)[T][number];
type NodeDefaultElementType = {
  root: "div";
  header: typeof Header;
  apiRequest: typeof ApiRequest;
  swiperSlider: typeof SwiperSlider;
  topics: typeof Topics;
  iframe: typeof Iframe;
  link: "a";
  embedHtml: typeof Embed;
  progress: typeof AntdProgress;
  comment: typeof Comment;
  button: typeof Button;
};

type ReservedPropsType = "variants" | "args" | "overrides";
type NodeOverridesType<T extends NodeNameType> = Pick<
  PlasmicCenter__OverridesType,
  DescendantsType<T>
>;
type NodeComponentProps<T extends NodeNameType> =
  // Explicitly specify variants, args, and overrides as objects
  {
    variants?: PlasmicCenter__VariantsArgs;
    args?: PlasmicCenter__ArgsType;
    overrides?: NodeOverridesType<T>;
  } &
    // Specify variants directly as props
    Omit<PlasmicCenter__VariantsArgs, ReservedPropsType> &
    // Specify args directly as props
    Omit<PlasmicCenter__ArgsType, ReservedPropsType> &
    // Specify overrides for each element directly as props
    Omit<
      NodeOverridesType<T>,
      ReservedPropsType | VariantPropType | ArgPropType
    > &
    // Specify props for the root element
    Omit<
      Partial<React.ComponentProps<NodeDefaultElementType[T]>>,
      ReservedPropsType | VariantPropType | ArgPropType | DescendantsType<T>
    >;

function makeNodeComponent<NodeName extends NodeNameType>(nodeName: NodeName) {
  type PropsType = NodeComponentProps<NodeName> & { key?: React.Key };
  const func = function <T extends PropsType>(
    props: T & StrictProps<T, PropsType>
  ) {
    const { variants, args, overrides } = React.useMemo(
      () =>
        deriveRenderOpts(props, {
          name: nodeName,
          descendantNames: PlasmicDescendants[nodeName],
          internalArgPropNames: PlasmicCenter__ArgProps,
          internalVariantPropNames: PlasmicCenter__VariantProps
        }),
      [props, nodeName]
    );
    return PlasmicCenter__RenderFunc({
      variants,
      args,
      overrides,
      forNode: nodeName
    });
  };
  if (nodeName === "root") {
    func.displayName = "PlasmicCenter";
  } else {
    func.displayName = `PlasmicCenter.${nodeName}`;
  }
  return func;
}

export const PlasmicCenter = Object.assign(
  // Top-level PlasmicCenter renders the root element
  makeNodeComponent("root"),
  {
    // Helper components rendering sub-elements
    header: makeNodeComponent("header"),
    apiRequest: makeNodeComponent("apiRequest"),
    swiperSlider: makeNodeComponent("swiperSlider"),
    topics: makeNodeComponent("topics"),
    iframe: makeNodeComponent("iframe"),
    link: makeNodeComponent("link"),
    embedHtml: makeNodeComponent("embedHtml"),
    progress: makeNodeComponent("progress"),
    comment: makeNodeComponent("comment"),
    button: makeNodeComponent("button"),

    // Metadata about props expected for PlasmicCenter
    internalVariantProps: PlasmicCenter__VariantProps,
    internalArgProps: PlasmicCenter__ArgProps
  }
);

export default PlasmicCenter;
